catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading || newValue == '') {
		return;
	}

	if(g_form.getValue('item_name') != ''){
		var result;
		var ga = new GlideAjax('x_314998_cbuildext.CatalogBuilderUtils'); 
		ga.addParam('sysparm_name','returnProxyRecord'); 
		ga.addParam('sysparm_id', g_form.getValue('item_name')); 
		ga.getXML(function(response) {  
			var answer = response.responseXML.documentElement.getAttribute("answer"); 
			result = JSON.parse(answer);
			g_form.setValue('assignment_group',result.group); 
			g_form.setValue('impact',result.impact);
			g_form.setValue('external_ci_name', result.ciname);
			g_form.setValue('ticket_type', result.ticket_type);
			g_form.setValue('external_ci_id', result.external_id);
		});
	}

}
